    public class Employee
    {
   
        private string name;
        private int empNo;
        private decimal basic;
        private short deptNo;
        public string Name
        {
            set
            {
                if (value != "")
                {
                    name = value;
                }
                else
                {
                    Console.Write("invalid!");
                }
            }
            get
            {
                return name;
            }


        }
        private static int temp=0;
       
           
     
        public int EmpNo
        {
            get
