        public decimal Basic
        {
            set
            {
                if (value < 100000 && value > 1000)
                {
                    basic = value;
                }
                else
                {
                    Console.WriteLine("Invalid!");
                }
            }
            get
            {
                return basic;
            }
        }
        public decimal getNetsalary()
        {
            decimal netSalary;
            netSalary = basic - 2000;
            return netSalary;
        }

        public short DeptNo
        {
            set
            {
                if (value > 0)
                {
                    deptNo = value;
                }
                else
                {
                    Console.WriteLine("Invalid");
                }
            }
            get
            {
                return deptNo;
            }
        }
      
        public Employee(string name="" ,decimal basic=0, short deptno=0)
        {
            temp++;
            empNo = temp;
            this.name = name;
            this.basic = basic; 
            this.deptNo = deptno;       
        }
